import React, {useRef, useState} from "react";
import Counter from "./components/Counter";
import ClassCounter from "./components/classCounter";

import './styles/App.css';
import PostItem from "./components/PostItem";
import PostList from "./components/PostList";
import MyButton from "./components/UI/button/MyButton";
import MyInput from "./components/UI/input/MyInput";
import DataFetching from "./components/DataFetching";

function App() {


    const [output, SetOutput] = useState('');

    const getTextFromApi = () => {
        fetch('http://localhost:8888/shop-sw/back-end/public/', {
        // fetch('http://localhost:80/shop-sw/back-end/public/', {
            method: 'POST',
            header: {
                'Content-Type': 'application/x-www-form-urlencoded',
            },
            body: JSON.stringify({action: 1})
        })
            .then(response => response.text())
            .then(response => {
                SetOutput(response)
            })
    };

    const data = JSON.stringify({firstname: '2', lastname: 'f'});

    var formData = new FormData();
    formData.append('data', data);

    const send = () =>
    {
        fetch("http://localhost:8888/shop-sw/back-end/public/",
        // fetch("http://localhost:80/shop-sw/back-end/public/",
            {
                method: 'POST',
                body: formData
            })
            .then(function (response) {
                return response.text();
            });
    }


    async function addProduct()
    {
        const sku = document.getElementById('sku').value;
        const name = document.getElementById('name').value;
        const price = document.getElementById('price').value;
        const productType = document.getElementById('productType').value;

        let formData = new FormData();
        formData.append('sku', sku);
        formData.append('name', name);
        formData.append('price', price);
        formData.append('productType', productType);

        const res = await fetch('http://localhost:8888/shop-sw/back-end/public/', {
            method: 'POST',
            body: formData
        })
        .then(function (response) {
            return response.text();
        });

        getProducts();
        // const data = await res.json();
        // //
        // if(data.status === true)
        // {
        //     getProducts();
        // }

    }

    const [posts, setPosts] = useState([]);

    const [products, setProducts] = useState([]);

    const [title, setTitle] = useState('');
    const bodyInputRef = useRef();

    const [post, setPost] = useState({title: '', body: ''});
    const [product, setProduct] = useState({id: '', sku: '', name: '', price: '', productType: ''});

    const addNewProduct = () => {
        setProducts([...products, {...product}])
    }

    async function getProducts()
    {
        let res = await fetch('http://localhost:8888/shop-sw/back-end/public/products');
        let results = await res.json();

        // document.querySelector('.product-list').innerHTML = '';
        setProducts([]);
        results.forEach((result) => {
            setProduct({id: result.id, sku: result.sku, name: result.name, price: result.price, productType: result.productType});
            setProducts([...products, product]);
            // console.log(result);
            // document.querySelector('.product-list').innerHTML += `
            //     <div class="card">
            //         <p>${product.id}, ${product.sku}, ${product.name}, ${product.price}, ${product.productType}</p>
            //     </div>
            // `
        })
    }

    const addNewPost = (e) => {
        e.preventDefault();
        // const newPost = {
        //     id: Date.now(),
        //     title,
        //     body: bodyInputRef.current.value
        // }
        setPosts([...posts, {...post, id: Date.now()}]);
        // setTitle('');
        // bodyInputRef.current.value = '';
        setPost({title: '', body: ''})
    }

    return (
    <div className="App">
        {/*<form>*/}
        {/*    <MyInput*/}
        {/*        value={post.title}*/}
        {/*        onChange={e => setPost({...post, title: e.target.value})}*/}
        {/*        type="text"*/}
        {/*        placeholder='Title'*/}
        {/*    />*/}
        {/*    <MyInput*/}
        {/*        value={post.body}*/}
        {/*        onChange={e => setPost({...post, body: e.target.value})}*/}
        {/*        type="text"*/}
        {/*        placeholder='Title'*/}
        {/*    />*/}

        {/*    /!*<MyInput*/}
        {/*        value={title}*/}
        {/*        onChange={e => setTitle(e.target.value)}*/}
        {/*        type="text"*/}
        {/*        placeholder='Title'*/}
        {/*    />*/}
        {/*    <MyInput*/}
        {/*        ref={bodyInputRef}*/}
        {/*        type="text"*/}
        {/*        placeholder='Description'*/}
        {/*    />*!/*/}
        {/*    <MyButton onClick={addNewPost}>Add</MyButton>*/}
        {/*</form>*/}

        {/*<PostList posts={posts} title="Post list 1" />*/}

        {/*{output}*/}
        {/*<button onClick={getTextFromApi}>Get</button>*/}
        {/*<button onClick={send}>Send</button>*/}

        {/*<button onClick={getProducts}>Load</button>*/}

        {/*<ProductList products={products}/>*/}

        <hr/>
        <DataFetching/>

    </div>
    );

}


export default App;
