import React, {useState} from 'react';
import axios from "axios";
import MyInput from "../UI/input/MyInput";
import MyButton from "../UI/button/MyButton";
import MySelect from "../UI/select/MySelect";
import classes from "../../styles/Product.module.css";
import {Link, useNavigate} from "react-router-dom";
import {useForm} from "react-hook-form";

const ProductForm = () => {

    const [product, setProduct] = useState({sku: '', name: '', price: '', productType: '', weight: '', size: '', height: '', width: '', length: ''});
    const {
        register,
        watch,
        formState: { errors },
        handleSubmit
    } = useForm({
        mode: "onBlur"
    });

    const onSubmit = (data) => {
        // alert(JSON.stringify(data));
        addNewProduct(JSON.stringify(data));
    };

    function formValid(e) {
        e.preventDefault();
        if(product.sku !== '' && product.name !== '' && product.productType !== '' && product.productType !== '') {
            switch (product.productType) {
                case 'Book':
                    if(product.weight !== '') addNewProduct();
                    break;
                case 'DVD':
                    if(product.size !== '') addNewProduct();
                    break;
                case 'Furniture':
                    if(product.length !== '' && product.height !== '' && product.width !== '') addNewProduct();
                    break;
                default:
                    break;
                    }
            }
    }

    const navigate = useNavigate();

    function addNewProduct(data) {
        axios.post('http://localhost:8888/shop-sw/back-end/public/', data)
            .then(response => {
                navigate('/');
                // console.log(response.data);
            });

        // setProduct({sku: '', name: '', price: '', productType: '', weight: '', size: '', height: '', width: '', length: ''});
    }

    return (
        <div>
            <form id="product_form" onSubmit={handleSubmit(onSubmit)}>
                <header>
                    <div className="container">
                        <h2>Product Add</h2>
                        <div className={classes.buttons}>
                            {/*<MyButton onClick={formValid} onSubmit={handleSubmit(onSubmit)}>Save</MyButton>*/}
                            <MyButton type="submit">Save</MyButton>
                            <Link className={classes.link} to="/"><MyButton>Cancel</MyButton></Link>
                        </div>
                    </div>
                    <hr/>
                </header>
                <div>
                    <label>SKU</label>
                    <MyInput
                        {...register('sku', {
                            required: "Field is required"
                        })}
                        id="sku"
                        value={product.sku}
                        onChange={e => setProduct({...product, sku: e.target.value})}
                        type="text"
                        placeholder='SKU'
                        style = {errors?.sku && {border: '1px solid red', color: 'red'}}
                    />
                    <div>
                        {errors?.sku && <p>{errors?.sku?.message || "Error!"}</p>}
                    </div>
                </div>

                <div>
                    <label>Name</label>
                    <MyInput
                        {...register('name', {
                            required: "Field is required"
                        })}
                        id="name"
                        value={product.name}
                        onChange={e => setProduct({...product, name: e.target.value})}
                        type="text"
                        placeholder='Name'
                    />
                    <div>
                        {errors?.name && <p>{errors?.name?.message || "Error!"}</p>}
                    </div>
                </div>

                <div>
                    <label>Price ($)</label>
                    <MyInput
                        {...register('price', {
                            required: "Field is required"
                        })}
                        id="price"
                        value={product.price}
                        onChange={e => setProduct({...product, price: e.target.value})}
                        type="number"
                        placeholder='Price'
                    />
                    <div>
                        {errors?.price && <p>{errors?.price?.message || "Error!"}</p>}
                    </div>
                </div>

                <div>
                    <label>Type Switcher</label>
                    {/*<MySelect*/}
                    {/*    {...register('productType', {*/}
                    {/*        required: "Field is required"*/}
                    {/*    })}*/}
                    {/*    id="productType"*/}
                    {/*    value={('productType')}*/}
                    {/*    // value={product.productType}*/}
                    {/*    // onChange={e => setProduct({...product, productType: e})}*/}
                    {/*    defaultValue="Type Switcher"*/}
                    {/*    options={[*/}
                    {/*        {id: 'Book', value: 'Book', name: 'Book'},*/}
                    {/*        {id: 'DVD', value: 'DVD', name: 'DVD'},*/}
                    {/*        {id: 'Furniture', value: 'Furniture', name: 'Furniture'}*/}
                    {/*    ]}*/}
                    {/*/>*/}
                    <select id="productType" {...register('productType', {required: 'Field is required'})}>
                        <option selected value="" disabled>Type Switcher</option>
                        <option id="Book" value="Book">Book</option>
                        <option id="DVD" value="DVD">DVD</option>
                        <option id="Furniture" value="Furniture">Furniture</option>
                    </select>
                    <div>
                        {errors?.productType && <p>{errors?.productType?.message || "Error!"}</p>}
                    </div>
                </div>

                {
                    watch('productType') === 'Book' ? (
                    // product.productType === 'Book' ? (
                        <div>
                            <label>Weight (KG)</label>
                            <MyInput
                                {...register('weight', {
                                    required: "Field is required"
                                })}
                                id="weight"
                                value={product.weight}
                                onChange={e => setProduct({...product, weight: e.target.value})}
                                type="number"
                                placeholder='Weight'
                            />
                            <div>
                                {errors?.weigth && <p>{errors?.weight?.message || "Error!"}</p>}
                            </div>
                        </div>
                    ) : watch('productType') === 'DVD' ? (
                        <div>
                            <label>Size (MB)</label>
                            <MyInput
                                {...register('size', {
                                    required: "Field is required"
                                })}
                                id="size"
                                value={product.size}
                                onChange={e => setProduct({...product, size: e.target.value})}
                                type="number"
                                placeholder='Size'
                            />
                            <div>
                                {errors?.size && <p>{errors?.size?.message || "Error!"}</p>}
                            </div>
                        </div>
                    ) : watch('productType') === 'Furniture' ? (
                        <div>
                            <div>
                                <label>Height (CM)</label>
                                <MyInput
                                    {...register('height', {
                                        required: "Field is required"
                                    })}
                                    id="height"
                                    value={product.height}
                                    onChange={e => setProduct({...product, height: e.target.value})}
                                    type="number"
                                    placeholder='Height'
                                />
                                <div>
                                    {errors?.height && <p>{errors?.height?.message || "Error!"}</p>}
                                </div>
                            </div>
                            <div>
                                <label>Width (CM)</label>
                                <MyInput
                                    {...register('width', {
                                        required: "Field is required"
                                    })}
                                    id="width"
                                    value={product.width}
                                    onChange={e => setProduct({...product, width: e.target.value})}
                                    type="number"
                                    placeholder='Width'
                                />
                                <div>
                                    {errors?.width && <p>{errors?.width?.message || "Error!"}</p>}
                                </div>
                            </div>
                            <div>
                                <label>Length (CM)</label>
                                <MyInput
                                    {...register('length', {
                                        required: "Field is required"
                                    })}
                                    id="length"
                                    value={product.length}
                                    onChange={e => setProduct({...product, length: e.target.value})}
                                    type="number"
                                    placeholder='Length'
                                />
                                <div>
                                    {errors?.length && <p>{errors?.length?.message || "Error!"}</p>}
                                </div>
                            </div>
                        </div>
                    ) : ( <p></p> )
                }
            </form>
        </div>
    );
};

export default ProductForm;
