import React from 'react';
import classes from "../../../styles/MySelect.module.css";

const MySelect = ({reg, options, defaultValue, value, onChange, ...props}) => {
    return (
        <select
            {...props}
            className={classes.mySelect}
        >
            <option selected disabled value="">{defaultValue}</option>
            {options.map(option =>
                <option id={option.id} key={option.value} value={option.value}>
                    {option.name}
                </option>
            )}
        </select>
    );
};
