import React, {useRef, useState} from "react";

import './styles/App.css';
import classes from './styles/App.css';
import DataFetching from "./components/DataFetching";
import ProductForm from "./components/UI/product/ProductForm";

function App() {

    const [product, setProduct] = useState({sku: '', name: '', price: '', productType: ''});

    const addNewProduct = (e) => {
        e.preventDefault();
        let data = new FormData();
        data.append('sku', product.sku);
        data.append('name', product.name);
        data.append('price', product.price);
        data.append('productType', product.productType);

        fetch("http://localhost:8888/shop-sw/back-end/public/",
            {
                method: 'POST',
                body: data
            })
            .then(function (response) {
                return response.text();
            });

        setProduct({sku: '', name: '', price: '', productType: ''});
    }

    return (
    <div className="App">
        <header>
            <h2>Product List</h2>
            <div className="buttons">
            </div>

            <hr/>
        </header>

        <DataFetching/>

        <br/>
        <br/>
        <br/>
    </div>
    );

}


export default App;


