import React, {useRef, useState} from "react";

import './styles/App.css';
import classes from './styles/App.css';
import MyButton from "./components/UI/button/MyButton";
import MyInput from "./components/UI/input/MyInput";
import DataFetching from "./components/DataFetching";

function App() {

    async function addProduct()
    {
        const sku = document.getElementById('sku').value;
        const name = document.getElementById('name').value;
        const price = document.getElementById('price').value;
        const productType = document.getElementById('productType').value;

        let formData = new FormData();
        formData.append('sku', sku);
        formData.append('name', name);
        formData.append('price', price);
        formData.append('productType', productType);

        const res = await fetch('http://localhost:8888/shop-sw/back-end/public/', {
            method: 'POST',
            body: formData
        })
        .then(function (response) {
            return response.text();
        });
    }


    const [product, setProduct] = useState({sku: '', name: '', price: '', productType: ''});

    const addNewProduct = (e) => {
        e.preventDefault();
        let data = new FormData();
        data.append('sku', product.sku);
        data.append('name', product.name);
        data.append('price', product.price);
        data.append('productType', product.productType);

        fetch("http://localhost:8888/shop-sw/back-end/public/",
            {
                method: 'POST',
                body: data
            })
            .then(function (response) {
                return response.text();
            });

        setProduct({sku: '', name: '', price: '', productType: ''});
    }

    return (
    <div className="App">
        <form>
            <MyInput
                value={product.sku}
                onChange={e => setProduct({...product, sku: e.target.value})}
                type="text"
                placeholder='SKU'
            />
            <MyInput
                value={product.name}
                onChange={e => setProduct({...product, name: e.target.value})}
                type="text"
                placeholder='Name'
            />
            <MyInput
                value={product.price}
                onChange={e => setProduct({...product, price: e.target.value})}
                type="number"
                placeholder='Price'
            />
            <MyInput
                value={product.productType}
                onChange={e => setProduct({...product, productType: e.target.value})}
                type="text"
                placeholder='Product type'
            />
            <MyButton onClick={addNewProduct}>Add</MyButton>
        </form>

        <header>
            <h2>Product List</h2>
            <div className="buttons">
            </div>

            <hr/>
        </header>

        <DataFetching/>

    </div>
    );

}


export default App;


