import React, { useState } from 'react';
import axios from "axios";
import MyInput from "../UI/input/MyInput";
import MyButton from "../UI/button/MyButton";
import classes from "../../styles/Product.module.css";
import { Link, useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";


const ProductForm = () => {

    const [product, setProduct] = useState({sku: '', name: '', price: '', productType: '', weight: '', size: '', height: '', width: '', length: ''});
    const {
        register,
        watch,
        formState: { errors, submitCount, isValid },
        handleSubmit
    } = useForm({
        mode: "onBlur"
    });

    const onSubmit = (data) => {
        addNewProduct(JSON.stringify(data));
    };

    const navigate = useNavigate();

    function addNewProduct(data) {
        axios.post('http://localhost:8888/shop-sw/back-end/public/', data)
            .then(response => {
                navigate('/');
            });
    }

    return (
        <div>
            <form id="product_form" onSubmit={handleSubmit(onSubmit)}>
                <header>
                    <div className="container">
                        <h2>Product Add</h2>
                        <div className={classes.buttons}>
                            <MyButton type="submit">Save</MyButton>
                            <Link className={classes.link} to="/"><MyButton>Cancel</MyButton></Link>
                        </div>
                    </div>
                    <hr/>
                </header>
                {!isValid && submitCount > 0 && <div className={classes.errMsg}>Please, submit required data</div>}
                <div>
                    <label>SKU</label>
                    <MyInput
                        {...register('sku', {
                            required: true
                        })}
                        id="sku"
                        // value={product.sku}
                        // onChange={e => setProduct({...product, sku: e.target.value})}
                        // type="text"
                        placeholder='SKU'
                        style = {errors?.sku && {border: '1px solid red', }}
                    />
                </div>

                <div>
                    <label>Name</label>
                    <MyInput
                        {...register('name', {
                            required: true
                        })}
                        id="name"
                        placeholder='Name'
                        style = {errors?.name && {border: '1px solid red', }}
                    />
                </div>

                <div>
                    <label>Price ($)</label>
                    <MyInput
                        {...register('price', {
                            required: true,
                            pattern: {
                                value: /^(0|[1-9]\d*)(\.\d+)?$/,
                                message: 'Please, provide the data of indicated type'
                            }
                        })}
                        id="price"
                        placeholder='Price'
                        style = {errors?.price && {border: '1px solid red'}}
                    />
                    <p style={{display: 'inline-block', color: 'red', marginLeft: 10}}>
                        {errors?.price && <p>{errors?.price?.message || "Error!"}</p>}
                    </p>
                </div>

                <div>
                    <label>Type Switcher</label>
                    {/*<MySelect*/}
                    {/*    {...register('productType', {*/}
                    {/*        required: "Field is required"*/}
                    {/*    })}*/}
                    {/*    id="productType"*/}
                    {/*    value={('productType')}*/}
                    {/*    // value={product.productType}*/}
                    {/*    // onChange={e => setProduct({...product, productType: e})}*/}
                    {/*    defaultValue="Type Switcher"*/}
                    {/*    options={[*/}
                    {/*        {id: 'Book', value: 'Book', name: 'Book'},*/}
                    {/*        {id: 'DVD', value: 'DVD', name: 'DVD'},*/}
                    {/*        {id: 'Furniture', value: 'Furniture', name: 'Furniture'}*/}
                    {/*    ]}*/}
                    {/*/>*/}
                    <select style = {errors?.productType && {border: '1px solid red', color: 'red'}} id="productType" {...register('productType', {required: 'Field is required'})} >
                        <option selected value="" disabled>Type Switcher</option>
                        <option id="Book" value="Book">Book</option>
                        <option id="DVD" value="DVD">DVD</option>
                        <option id="Furniture" value="Furniture">Furniture</option>
                    </select>
                </div>

                {
                    watch('productType') === 'Book' ? (
                        <div>
                            <label>Weight (KG)</label>
                            <MyInput
                                {...register('weight', {
                                    required: true,
                                    pattern: {
                                        value: /^(0|[1-9]\d*)(\.\d+)?$/,
                                        message: 'Please, provide the data of indicated type'
                                    }
                                })}
                                id="weight"
                                placeholder='Weight'
                                style = {errors?.weight && {border: '1px solid red', }}
                            />
                        </div>
                    ) : watch('productType') === 'DVD' ? (
                        <div>
                            <label>Size (MB)</label>
                            <MyInput
                                {...register('size', {
                                    required: true,
                                    pattern: {
                                        value: /^(0|[1-9]\d*)(\.\d+)?$/,
                                        message: 'Please, provide the data of indicated type'
                                    }
                                })}
                                id="size"
                                placeholder='Size'
                                style = {errors?.size && {border: '1px solid red', }}
                            />
                        </div>
                    ) : watch('productType') === 'Furniture' ? (
                        <div>
                            <div>
                                <label>Height (CM)</label>
                                <MyInput
                                    {...register('height', {
                                        required: true,
                                        pattern: {
                                            value: /^(0|[1-9]\d*)(\.\d+)?$/,
                                            message: 'Please, provide the data of indicated type'
                                        }
                                    })}
                                    id="height"
                                    placeholder='Height'
                                    style = {errors?.height && {border: '1px solid red', }}
                                />
                            </div>
                            <div>
                                <label>Width (CM)</label>
                                <MyInput
                                    {...register('width', {
                                        required: true,
                                        pattern: {
                                            value: /^(0|[1-9]\d*)(\.\d+)?$/,
                                            message: 'Please, provide the data of indicated type'
                                        }
                                    })}
                                    id="width"
                                    placeholder='Width'
                                    style = {errors?.width && {border: '1px solid red', }}
                                />
                            </div>
                            <div>
                                <label>Length (CM)</label>
                                <MyInput
                                    {...register('length', {
                                        required: true,
                                        pattern: {
                                            value: /^(0|[1-9]\d*)(\.\d+)?$/,
                                            message: 'Please, provide the data of indicated type'
                                        }
                                    })}
                                    id="length"
                                    placeholder='Length'
                                    style = {errors?.length && {border: '1px solid red', }}
                                />
                            </div>
                        </div>
                    ) : ( <p></p> )
                }
            </form>
        </div>
    );
};

export default ProductForm;
