import React, {useState, useEffect} from "react";
import axios from "axios";
import Product from "./product/Product";
import classes from "./product/Product.module.css";

function DataFetching() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8888/shop-sw/back-end/public/')
            .then(response => {
                setProducts(response.data)
            });
    });

    const deleteProduct = (id) => {
        axios.delete(`http://localhost:8888/shop-sw/back-end/public/${id}`)
            .then(response => {
                return response
            });
    }


    const [arrayId, setArrayId] = useState([]);

    const deleteProducts = () => {
        let data = '';
        arrayId.forEach(id => {
            data += id + ',';
        });

        axios.delete(`http://localhost:8888/shop-sw/back-end/public/${data}`)
            .then(response => {
                return response
            });
    }

    const setIdToDelete = (v) => {
        const found = arrayId.find(obj => {
            return obj === v;
        });
        if(found) {
            setArrayId(arrayId => arrayId.filter(arrayId => arrayId !== v));
        } else {
            setArrayId([...arrayId, v]);
        }
    }

    return (
        <div>
            <button onClick={deleteProducts}>MASS DELETE</button>
            <div className={classes.productList}>
                {/*{products.map(product => <Product key={product.id} onClick={() => deleteProduct(product.id)} id={product.id} sku={product.sku} name={product.name} price={product.price} productType={product.productType}/>)}*/}
                {products.map(product => <Product onChange={() => setIdToDelete(product.id)} key={product.id} id={product.id} sku={product.sku} name={product.name} price={product.price} productType={product.productType}/>)}
            </div>
        </div>
    )
}

export default DataFetching
