import React, {useState} from 'react';
import axios from "axios";
import MyInput from "../UI/input/MyInput";
import MyButton from "../UI/button/MyButton";
import MySelect from "../UI/select/MySelect";
import classes from "./Product.module.css";
import {Link} from "react-router-dom";

const ProductForm = () => {

    const [product, setProduct] = useState({sku: '', name: '', price: '', productType: '', weight: '', size: '', height: '', width: '', length: ''});

    function formValid(e) {
        e.preventDefault();
        if(product.sku !== '' && product.name !== '' && product.productType !== '' && product.productType !== '') {
            addNewProduct();
        }
    }
    function addNewProduct() {
        axios.post('http://localhost:8888/shop-sw/back-end/public/', product)
            .then(response => {
                console.log(response.data);
            });

        setProduct({sku: '', name: '', price: '', productType: '', weight: '', size: '', height: '', width: '', length: ''});
    }

    return (
        <div>
            <header>
                <h2>Product Add</h2>
                <div className={classes.buttons}>
                    <button><Link className={classes.link} to="/">Cancel</Link></button>
                </div>
                <hr/>
            </header>
            <form>
                <MyInput
                    value={product.sku}
                    onChange={e => setProduct({...product, sku: e.target.value})}
                    type="text"
                    placeholder='SKU'
                />
                <MyInput
                    value={product.name}
                    onChange={e => setProduct({...product, name: e.target.value})}
                    type="text"
                    placeholder='Name'
                />
                <MyInput
                    value={product.price}
                    onChange={e => setProduct({...product, price: e.target.value})}
                    type="number"
                    placeholder='Price'
                />
                <MySelect
                    value={product.productType}
                    onChange={e => setProduct({...product, productType: e})}
                    defaultValue="Product Type"
                    options={[
                        {value: 'book', name: 'book'},
                        {value: 'dvd', name: 'dvd'},
                        {value: 'furniture', name: 'furniture'}
                    ]}
                />
                {
                    product.productType === 'book' ? (
                        <MyInput
                            value={product.weight}
                            onChange={e => setProduct({...product, weight: e.target.value})}
                            type="number"
                            placeholder='Weight'
                        />
                    ) : product.productType === 'dvd' ? (
                        <MyInput
                            value={product.size}
                            onChange={e => setProduct({...product, size: e.target.value})}
                            type="number"
                            placeholder='Size'
                        />
                    ) : product.productType === 'furniture' ? (
                        <div>
                            <MyInput
                                value={product.height}
                                onChange={e => setProduct({...product, height: e.target.value})}
                                type="number"
                                placeholder='Height'
                            />
                            <MyInput
                                value={product.width}
                                onChange={e => setProduct({...product, width: e.target.value})}
                                type="number"
                                placeholder='Width'
                            />
                            <MyInput
                                value={product.length}
                                onChange={e => setProduct({...product, length: e.target.value})}
                                type="number"
                                placeholder='Length'
                            />
                        </div>
                    ) : ( <p></p> )
                }
                <MyButton onClick={formValid}>Add</MyButton>
            </form>
        </div>
    );
};

export default ProductForm;
