import React, {useState, useEffect} from "react";
import axios from "axios";
import Product from "./product/Product";
import classes from "./product/Product.module.css";
import MySelect from "./UI/select/MySelect";
import product from "./product/Product";
import MyInput from "./UI/input/MyInput";
import MyButton from "./UI/button/MyButton";

function DataFetching() {
    const [products, setProducts] = useState([]);
    const [processedProducts, setProcessedProducts] = useState([]);
    const [selectedSort, setSelectedSort] = useState('');

    useEffect(() => {
        axios.get('http://localhost:8888/shop-sw/back-end/public/')
            .then(response => {
                setProducts(response.data)
            });
    });



    const [arrayId, setArrayId] = useState([]);

    const deleteProducts = () => {
        let data = '';
        arrayId.forEach(id => {
            data += id + ',';
        });

        axios.delete(`http://localhost:8888/shop-sw/back-end/public/${data}`)
            .then(response => {
                return response
            });

        sortProducts(sortProducts);
    }

    const setIdToDelete = (id) => {
        const found = arrayId.find(obj => {
            return obj === id;
        });
        if(found) {
            setArrayId(arrayId => arrayId.filter(arrayId => arrayId !== id));
        } else {
            setArrayId([...arrayId, id]);
        }
    }

    const sortProducts = (sort) => {
        setSelectedSort(sort);
        setProcessedProducts([...products].sort((a, b) => a[sort].localeCompare(b[sort])));
    }

    return (
        <div>
            <div style={{display: 'inline-block', marginRight: '73%'}} >
                <MySelect
                    value={selectedSort}
                    onChange={sortProducts}
                    defaultValue="Sorting"
                    options={[
                        {value: 'sku', name: 'by SKU'},
                        {value: 'name', name: 'by Name'}
                    ]}
                />
                <button onClick={() => setSelectedSort('')}>Reset</button>
            </div>
            <button onClick={deleteProducts}>MASS DELETE</button>
            <div className={classes.productList}>
                {selectedSort !== ''
                    ?
                    processedProducts.map(
                        product => product.id != null
                            ?
                            <Product onChange={() => setIdToDelete(product.id)} key={product.id} id={product.id} sku={product.sku} name={product.name} price={product.price} productType={product.productType}/>
                            :
                            <h1 style={{textAlign: 'center'}}>
                                Products not found
                            </h1>
                    )
                    :
                    products.map(
                    product => product.id != null
                    ?
                    <Product onChange={() => setIdToDelete(product.id)} key={product.id} id={product.id} sku={product.sku} name={product.name} price={product.price} productType={product.productType}/>
                    :
                    <h1 style={{textAlign: 'center'}}>
                    Products not found
                    </h1>
                    )
                }
                {/*{sortedProducts.map(*/}
                {/*    product => product.id != null*/}
                {/*        ?*/}
                {/*        <Product onChange={() => setIdToDelete(product.id)} key={product.id} id={product.id} sku={product.sku} name={product.name} price={product.price} productType={product.productType}/>*/}
                {/*        :*/}
                {/*        <h1 style={{textAlign: 'center'}}>*/}
                {/*            Products not found*/}
                {/*        </h1>*/}
                {/*)}*/}
            </div>
        </div>
    )
}

export default DataFetching
