import React, {useState, useEffect, useMemo} from "react";
import axios from "axios";
import Product from "./product/Product";
import classes from "./product/Product.module.css";
import MySelect from "./UI/select/MySelect";
import product from "./product/Product";
import MyInput from "./UI/input/MyInput";
import MyButton from "./UI/button/MyButton";

function DataFetching() {
    const [products, setProducts] = useState([]);
    const [selectedSort, setSelectedSort] = useState('');
    const [searchQuery, setSearchQuery] = useState('');


    const sortedProducts = useMemo(() => {
        if(products.length > 0) {
            if(selectedSort) {
                return [...products].sort((a, b) => a[selectedSort].localeCompare(b[selectedSort]));
            }
            return products;
        }
        return 'false';
    }, [selectedSort, products]);

    const processedProducts = useMemo(() => {
        if(sortedProducts != false) {
            return sortedProducts.filter(product => product.name.toLowerCase().includes(searchQuery.toLowerCase()));
        }
        return [];
    }, [searchQuery, sortedProducts]);


    useEffect(() => {
        axios.get('http://localhost:8888/shop-sw/back-end/public/')
            .then(response => {
                setProducts(response.data);
            });
        // if(products.length !== processedProducts.length) {
        //     if(selectedSort === '') {
        //         setProcessedProducts([...products]);
        //     } else {
        //         sortProducts(selectedSort);
        //     }
        // }
    });

    const [arrayId, setArrayId] = useState([]);

    const deleteProducts = () => {
        let data = '';
        arrayId.forEach(id => {
            data += id + ',';
        });

        axios.delete(`http://localhost:8888/shop-sw/back-end/public/${data}`)
            .then(response => {
                return response
            });
    }

    const setIdToDelete = (id) => {
        const found = arrayId.find(obj => {
            return obj === id;
        });
        if(found) {
            setArrayId(arrayId => arrayId.filter(arrayId => arrayId !== id));
        } else {
            setArrayId([...arrayId, id]);
        }
    }

    const resetSorting = () => {
        setSelectedSort('');
        // setProcessedProducts([...products]);
    }

    const sortProducts = (sort) => {
        setSelectedSort(sort);
        // setProcessedProducts([...products].sort((a, b) => a[sort].localeCompare(b[sort])));
    }

    return (
        <div>
            <div style={{display: 'inline-block', marginRight: '66%'}} >
                <MyInput
                    value={searchQuery}
                    onChange={e => setSearchQuery(e.target.value)}
                    placeholder="Search"
                />
                <br/>
                <MySelect
                    value={selectedSort}
                    onChange={sortProducts}
                    defaultValue="Sorting"
                    options={[
                        {value: 'sku', name: 'by SKU'},
                        {value: 'name', name: 'by Name'}
                    ]}
                />
                <button onClick={resetSorting}>Reset</button>
            </div>
            <button onClick={deleteProducts}>MASS DELETE</button>
            <div className={classes.productList}>
                {/*{processedProducts.map(*/}
                {/*    product => product.id != null*/}
                {/*        ?*/}
                {/*        <Product onChange={() => setIdToDelete(product.id)} key={product.id} id={product.id} sku={product.sku} name={product.name} price={product.price} productType={product.productType}/>*/}
                {/*        :*/}
                {/*        <h1 style={{textAlign: 'center'}}>*/}
                {/*            Products not found*/}
                {/*        </h1>*/}
                {/*)}*/}
                {processedProducts.length
                    ?
                    processedProducts.map((product) =>
                        <Product onChange={() => setIdToDelete(product.id)} key={product.id} id={product.id} sku={product.sku} name={product.name} price={product.price} productType={product.productType}/>
                    )
                    :
                    <h1 style={{textAlign: 'center'}}>
                        Products not found
                    </h1>
                }
            </div>
        </div>
    )
}

export default DataFetching
